<SCRIPT LANGUAGE="JavaScript" type="text/javascript"><!--/* The isEmpty and isWhitespace functions were taken straight from Netscape's JavaScript development site, http://developer.netscape.com.*/// whitespace charactersvar whitespace = " \t\n\r";/****************************************************************/// Check whether string s is empty.function isEmpty(s){ return ((s == null) || (s.length == 0)) }/****************************************************************/function isWhitespace (s){var i;// Is s empty?if (isEmpty(s)) return true;// Search through string's characters one by one// until we find a non-whitespace character.// When we do, return false; if we don't, return true.for (i = 0; i < s.length; i++){     // Check that current character isn't whitespace.     var c = s.charAt(i);     if (whitespace.indexOf(c) == -1) return false;}// All characters are whitespace.return true;                                  }                               /****************************************************************/function ForceEntry(val, str) {var strInput = new String(val.value);if (isWhitespace(strInput)) {     alert(str);     return false;} else     return true;                                  }      /****************************************************************/ function ValidateData() {var CanSubmit = false;// Check to make sure that the form fields are not empty.CanSubmit = ForceEntry(document.forms[0].firstname,"Please enter your first name."); // Check to make sure required information exists.           if (CanSubmit) CanSubmit = ForceEntry(document.forms[0].lastname,"Please enter your last name.");           if (CanSubmit) CanSubmit = ForceEntry(document.forms[0].employer,"Please enter your employer or organization.");           if (CanSubmit) CanSubmit = ForceEntry(document.forms[0].email,"Please enter your e-mail so we may send you a confirmation. We value your privacy and will never sell, rent, or share your information.");            return CanSubmit;                     }--> </SCRIPT>