<?$dbsec="documentsubcategory";	@mysql_select_db("$database") or die( "Unable to open database"); 	$queryselectionsec = "SELECT subcategoryid,subcategoryname from $dbsec order by subcategoryid";	$resultselectionsec = mysql_query($queryselectionsec);?><?phpif (isset($subcategory)) {	while(list($subcategoryid,$subcategoryname) = mysql_fetch_row($resultselectionsec)) {$code3 = htmlentities("$subcategoryid", ENT_QUOTES);	print "<option value=$code3";	if ($subcategoryid == $subcategory) echo " selected";    	print ">$subcategoryname</option>\n";}} // end if issetelse 	while(list($subcategoryid,$subcategoryname) = mysql_fetch_row($resultselectionsec)) {$code3 = htmlentities("$subcategoryid", ENT_QUOTES);	print "<option value=$code3>$subcategoryname</option>\n";}?><?mysql_close();?>